import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component{

  constructor(props){ 
    super(props);
    this.state={
      newItem:"",
      list:[]
    }
  }

  addItem(todovalue){

    if(todovalue!==""){
      const newItem={
        id:Date.now(),
        value:todovalue,
        isDone:false
      }
      const list=[...this.state.list];
      list.push(newItem);

      this.setState({
        list,
        newItem:""
      });
    }
  }
  deleteItem(id){
    const list=[...this.state.list];
    const updatedlist=list.filter(item => item.id !== id);
    this.setState({
      list:updatedlist,
    });
  }

  update(input){
    this.setState({
      newItem:input
    })
  }

  render(){
    return(
      <div>
        <img src={logo} width="250" height="250 "className="logo"/>
        <h1 className="app-title"> LCO ToDo App</h1>
        <div className="container">
          add an item....
